extractGenus<-function(speciesNames, splitCharacter=" ") {
  genus<-character(length=length(speciesNames))
  tt<-strsplit(speciesNames, split=splitCharacter)
  for(i in 1:length(speciesNames)) {
    genus[i]<-tt[[i]][1]
  }
  return(genus)
}

result<-extractGenus(phy$tip.label, splitCharacter=splitChar)
result<-as.matrix(result)
